INSERT INTO `tp_article` (`id`, `title`, `keywords`, `description`, `author`, `email`, `link_url`, `thumb`, `content`, `show_top`, `show_status`, `cate_id`, `addtime`) VALUES
(35, '漯河市妇幼保健院来我院参观交流', '漯河市妇幼保健院来我院参观交流', '漯河市妇幼保健院来我院参观交流', '', '', '', '20190301/6ae655ebb57adb97441a0ff87137184f.png', '<p><br/></p><p style="margin-top: 20px; line-height: 1.5em;"><span style="color: rgb(51, 51, 51); font-family: 仿宋_GB2312; font-size: 18px; text-align: justify; text-indent: 48px;  ">&nbsp; &nbsp; <span style="color: rgb(51, 51, 51); text-align: justify; text-indent: 48px;   font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;">&nbsp; &nbsp;12月27日上午，漯河市妇幼保健院王晓丽副院长带队一行七人来我院参观交流。我院党委书记张建芳及相关科室负责人热情接待了来访人员，座谈会在21楼会议室进行。</span></span></p><p style="margin-top: 20px; line-height: 1.5em;"><span style="font-family: 微软雅黑, " microsoft="" background-color:="" color:="" text-align:="" text-indent:="">&nbsp; &nbsp; &nbsp; &nbsp;张书记首先对漯河市妇幼保健院各位同仁的到来表示热烈的欢迎。随后学习考察组一行人在各科室负责人的带领下参观了儿童保健科、女性健康管理中心、产科门诊、乳腺科、儿科病区等相关科室，对我院“四大部”标准化建设进行深入了解。</span></p><p style="margin-top: 20px; line-height: 1.5em;"><span style="font-family: 微软雅黑, " microsoft="" background-color:="" color:="" text-align:="" text-indent:="">&nbsp; &nbsp; &nbsp; 参观结束后，王晓丽副院长对我院的热情接待表示衷心的感谢，并对我院在各方面工作中取得的成绩表示赞赏。希望两院能进一步加深合作，共同进步，共谋发展。</span></p><p style="margin-top: 10px;"><span style="color: rgb(51, 51, 51); font-family: 仿宋_GB2312; font-size: 18px; text-align: justify; text-indent: 48px;  "><br/></span><br/></p>', 0, 1, 28, 1551407626),
