--
-- 表的结构 `tp_doctor`
--

CREATE TABLE IF NOT EXISTS `tp_doctor` (
  `id` smallint(6) NOT NULL AUTO_INCREMENT,
  `name` varchar(10) NOT NULL COMMENT '姓名',
  `ranks` varchar(10) NOT NULL COMMENT '职称',
  `department` smallint(6) NOT NULL COMMENT '科室（1儿内科,2妇产科,3乳腺科4生殖遗传,5耳鼻喉科,6保健部,7儿外科,8口腔科,9医技辅助10）',
  `intro` varchar(500) NOT NULL COMMENT '简介',
  `skilled` varchar(500) NOT NULL COMMENT '擅长',
  `schedule` varchar(100) NOT NULL COMMENT '周日上午-周六下午(00a,00b,01a,01b,02a,02b,03a,03b,04a,04b,05a,05b,06a,06b)',
  `pic_url` varchar(100) NOT NULL COMMENT '缩略图地址',
  `addtime` int(11) NOT NULL COMMENT '添加时间',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 AUTO_INCREMENT=24 ;

--
-- 转存表中的数据 `tp_doctor`
--

INSERT INTO `tp_doctor` (`id`, `name`, `ranks`, `department`, `intro`, `skilled`, `schedule`, `pic_url`, `addtime`) VALUES
(1, '李青', '主任医师', 6, '22', '33', '01a,02a,03b,06a,06b', '20190222/5d52a0beb83cafae8ea9480bdb888f82.jpg', 0),
(2, '李文军', '副主任医师', 6, '小儿脑瘫、智力低下、脑发育迟缓、语言发育障碍的康复治疗，积累了丰富的诊断及康复经验，特别是对小儿孤独症的康复训练有丰富经验及独到见解。', '更多  更多 李文军，儿科副主任医师，1991年毕业于河南医科大学临床系，学士学位。毕业后即从事儿科临床工作，先后在新生儿科、普通病房、ICU、急诊室等科室工作，业务全面，技术熟练。2000年在北京医科大学进修小儿神经科，对小儿神经系统疾病如：癫痫、脑炎、高热惊厥、代谢病的诊治方面积累了丰富的经验，2003年赴佳木斯脑瘫疗育中心学习，2006年在北京学习小儿语言康复。', '01a,02a,03b,04a,05b', '20190222/d2976f2d5496d1f396102cb88f2999d9.jpg', 1550804180),
(4, '李亚娟', '主任医师', 1, '在抢救各种原因引起的多脏器功能衰竭、小儿各型中毒、各型休克、各型中毒、急腹症、心肺复苏等方面经验丰富', '急诊科主任，主任医师，任中华医学会河南省儿科分会急救学组委员，中国医师协会河南儿科分会常委，中西医结合协会河南急诊分会常委，中国医师协会河南急诊分会委员，河南省健康管理协会儿科分会委员，中国羊城儿童急诊急救论坛专家协作组成员。多次主办安阳市儿童急诊医学高峰论坛会。曾在北大妇儿医院重症专业深造，在抢救各种原因引起的多脏器功能衰竭、小儿各型中毒、各型休克、各型中毒、急腹症、心肺复苏等方面经验丰富，带领全科在小儿急救方面形成了一支训练有素的专业队伍，使我院儿童急救技术处于全市领先水平。在国家级、核心期刊发表论文30余篇，论著3部，主持科研4项。', '00b,01a,04a,06b', '20190228/a8305252909ae5bb409555f8f311bd21.jpg', 1551322380),
(5, '李海燕', '副主任医师', 1, '熟悉小儿常见疾病尤其是呼吸道疾病及小儿危重疾病的诊断和治疗。', '副主任医师，小儿呼吸专业硕士研究生毕业，2010年在北京儿童医院进修学习小儿重症医学专业半年，进修小儿纤维支气管镜四个月。目前在重症监护室工作，熟悉小儿常见疾病尤其是呼吸道疾病及小儿危重疾病的诊断和治疗。2013年5月首先在安阳市了开展小儿纤维支气管镜，对小儿呼吸道疑难重症疾病的诊断和治疗起到了很重要的作用。在国家级、省级杂志上发表文章数篇。', '01b,02a', '20190228/a2f0083472849428ccd1df9d346110d6.jpg', 1551324094),
(6, '商怀玉', '主任医师', 1, '对各类小儿内科疾病有丰富的临床经验，尤其擅长于各类小儿惊厥性疾病、小儿脑瘫、缺氧缺血性脑病等小儿神经系统疾病的诊断及治疗', '儿科主任兼神经康复一病区主任，主任医师，儿内科主任医师、中华医学会小儿癫痫学会委员，中华医学会河南省儿科分会委员，河南省残疾人康复委员会常委，安阳市儿科重点专科学科带头人。对各类小儿内科疾病有丰富的临床经验，尤其擅长于各类小儿惊厥性疾病、小儿脑瘫、缺氧缺血性脑病等小儿神经系统疾病的诊断及治疗。2003年带头成立了小儿神经康复科，为我市小儿神经系统疾病的诊治及小儿康复医学填补了空白。曾参加北京大学组织的《婴幼儿身心发育早期干预研究》科研课题，并获北京大学科技进步二等奖。主编《婴幼儿身心发育》一书，并多次在省级、国家级杂志上发表论文。', '01b,02a', '20190228/88175444da5a71b3976922575f56b24c.jpg', 1551325736),
(7, '韩文丽', '主任医师', 2, '\r\n主任医师，擅长：围产营养及妊娠期糖尿病，妊娠期甲状腺疾病的诊治，妊娠期感染性疾病的诊治，生殖内分泌疾病及女性盆底疾病的诊治。妇产科主任医师，河南省医院协会妇幼保健专业委员会委员，中华医学会河', '围产营养及妊娠期糖尿病，妊娠期甲状腺疾病的诊治，妊娠期感染性疾病的诊治，生殖内分泌疾病及女性盆底疾病的诊治', '02a,03a,04a', '20190305/ae30bbd7ac9aa4149a4b74919d53e89a.jpg', 1551768961),
(8, '张 静', '副主任医师', 1, '毕业于湖北武汉科技大学医学院临床医学系，本科学历。现任围产儿管理学科带头人。曾荣获‘文明职工’、‘先进个人’、‘最敬业医师’等荣誉称号；在国家级刊物发表过多篇学术论文、论著；先后取得国家人社...', '新生儿常见病、多发病的诊治，新生儿急救，新生儿保健，尤其对足月儿、早产儿母乳喂养过程遇到的各类问题指导', '01b,02b', '20190307/f3230819831afb66086883bbdf670e79.jpg', 1551919321),
(9, '刘书艳', '副主任医师', 1, '副主任医师， 2002年毕业于新乡医学院临床医学系，学士学位，毕业至今从事儿科临床工作，2011年在北京军区总医院附属八一儿童医院进修学习，并多次参加全国各种儿科培训班。熟练掌握儿科常见病、...', '\r\n新生儿常见病及危重症的诊治', '03a', '20190307/6019192e49d7db708496dbe5289dbbe6.jpg', 1551919484),
(10, '谢巧婷 ', '副主任医师', 2, '毕业于新乡医学院临床医学专业，学士学位。自2003年于我院工作至今，最初从事妇产科临床工作，后改行做病理诊断。只有与临床紧密结合，才能做出客观、准确的病理诊断，所以妇产科的临床经验为病理诊断...', '病理诊断', '01a,01b,02a,02b', '20190307/6bb755be91101bdca0be34324fbf3572.jpg', 1551921165),
(11, '宋淑敏', '副主任医师', 2, '宋淑敏，医学硕士，副主任医师，从事妇产科20余年，2015-2016于北京协和医院进修妇科和复兴医院进修宫腔镜，擅长腹腔镜下全子宫切、腹腔镜下子宫肌瘤剔除、宫腔镜下电切术、阴式子宫切除等微创..', '擅长腹腔镜下全子宫切、腹腔镜下子宫肌瘤剔除、宫腔镜下电切术、阴式子宫切除等微创手术等；擅长子宫内膜异位症、盆底疾病、恶性肿瘤、围绝经期疾病、异常子宫出血等疾病诊断与治疗。', '03a,04a', '20190307/fd26c3a583709e06a4fc20c86b1ae975.jpg', 1551922304),
(12, '刘玉献', '主任医师', 3, '中国医师协会乳腺疾病培训专家委员会委员；中国妇幼保健协会乳腺保健专业委员会常委；河南省预防医学会乳腺专业委员会常委；安阳市抗癌协会乳腺专业委员会副主任委员。参加工作以来一直从事临床外科工作。', '哺乳期乳腺炎的防治，乳腺癌前病变的筛查和干预，乳腺癌早期发现、早期诊断和早期治疗', '01a,01b,03a,03b,05a,05b', '20190307/fe2c20dd3e5288e6e4e93cdf891430e8.jpg', 1551922437),
(13, '岳桂英', '主任医师', 4, '主任医师，毕业于上海医科大学，医学学士学位，擅长诊治各科内分泌失调引起的不孕症，如月经不调、功血异常、卵巢综合症、闭经、输卵管慢性炎症性不孕、男性少精、弱精等原因引起的不孕症，开展优生优育指...', '月经不调、功血异常、卵巢综合症、闭经、输卵管慢性炎症性不孕、男性少精、弱精等原因引起的不孕症的诊治', '01a,01b,02a,02b,03a,03b,04a,04b,05a,05b', '20190307/a03efa789082a7318b411005b231da32.png', 1551922549),
(14, '张爱英', '主任医师', 4, '\r\n主任医师，毕业于新乡医学院医疗专业，曾在河南医科大学附院生殖中心进修不孕症及夫精人工受精技术。从事男女不孕不育症诊治及优生遗传咨询20余年，能针对孕前咨询、孕期咨询、习惯性流产、多次生育畸形...', '\r\n孕前咨询、孕期咨询、习惯性流产、多次生育畸形儿及不孕不育症的诊治', '00a,00b,01a,01b,03a,03b,05b,06a', '20190307/c4b00e2868d55e3312600c4edfac297f.png', 1551922614),
(15, '范鹏莺', '副主任医师', 4, '妇产科副主任医师，毕业于新乡医学院，学士学位，是我院不孕症科室创始人之一，从事不孕症工作20余年，尤其擅长诊断和治疗女性输卵管疾病、排卵障碍、子宫内膜疾病。具有丰富的临床经验，对不孕症的疑难...', '\r\n诊断和治疗女性输卵管疾病、排卵障碍、子宫内膜疾病', '01a,01b,02a,02b,03a,03b,04a,04b,05a,05b,06a,06b', '20190307/f2dc1967f10a0c48df16758dd68d924a.png', 1551922688),
(16, '陈春光 ', '主任医师', 5, '主治医师，毕业于辽宁医学院耳鼻咽喉头颈外科学专业。从事耳鼻咽喉-头颈外科临床工作10余年。曾先后于首都医科大学北京宣武医院、中国医学科学院肿瘤医院进修学习。擅长儿童扁桃体肥大、腺样体肥大，先..', '儿童扁桃体肥大、腺样体肥大，先天性耳前瘘管，声带息肉，甲状舌管囊肿，甲状腺肿瘤，鼾症，头颈部肿瘤及鼻腔鼻窦、中耳疾病的手术治疗', '01a,01b,03a,03b,05a,05b', '20190307/a00f916269d44e290cc9c571b69226a3.jpg', 1551925107),
(17, '常青峰', '副主任医师', 7, '院工会主席兼小儿外科主任 副主任医师，本科学历，河南省小儿外科学会委员 先后在南京、北京儿童医院进修学习 我院小儿外科创始人之一。专业特长：小儿普外科、泌尿外科、新生儿外科。在我市或我院率先...', '小儿普外科、泌尿外科、新生儿外科', '02a,02b', '20190307/36ab5bab5f682518d1f1e18ac41b59f3.png', 1551925650),
(18, '周光辉', '副主任医师', 7, '副主任医师，小儿泌尿外科学术带头人，医患关系办公室主任，医务科副科长，毕业于新乡医学院临床医学专业，学士学位。曾于北京儿童医院进修一年，擅长小儿泌尿外科、小儿普通外科及新生儿外科疾病的诊治。', '\r\n小儿泌尿外科、小儿普通外科及新生儿外科疾病的诊治', '03a,03b', '20190307/079369e5783ae1ad43c178da0d3b19bd.jpg', 1551925708),
(19, '陈 新', '主任医师', 8, '男，中共党员，主任医师。现任安阳市妇幼保健院党委副书记、院长，兼任中国医院协会妇幼保健院管理分会委员、河南医院协会妇幼保健院、儿童医院分会副主任委员、河南省预防医学会儿童保健、儿童心理卫生专...', '口腔内科、外科、修复科、正畸科疾病的诊断、治疗', '00a,03a', '20190307/4fbc1bbdb1172b9e9851aff6b06322dd.jpg', 1551926303),
(20, '龙兴华', '副主任医师', 8, '\r\n副主任医师，口腔科副主任，毕业于河南省郑州大学医学院，大学本科学历。潜心口腔临床工作二十余年。曾在北京市口腔医院，郑州省口腔医院等多地进修学习，撰写国家级论文10余篇。对口腔科常见病，疑难病...', '儿童口腔疾病的预防和治疗，各类缺失牙的修复，舌系带矫治术，粘液腺囊肿等手术，尤其擅长口腔各类错颌畸形的矫治及儿童颌面部畸形的活动矫治，如反颌（地包天），小下颌，间隙保持器等', '01a,01b,02a,02b,03a,03b,04a,04b,05a,05b,06a', '20190307/e5ff0f28a629da3f6e89b6c9ebb02861.jpg', 1551926394),
(21, '张巍', '副主任医师', 9, '超声诊断医师，门诊部副主任，郑大医学系本科，学士学位。从事超声诊断工作十年，曾在北京儿童医院小儿腹部超声科及心脏超声科进修学习，居我市开展小儿腹部超声诊断的前列。擅长小儿腹部超声诊断、小儿先...', '小儿腹部超声诊断、小儿先天性心脏病的诊断等', '00a,03a', '20190307/0f39d4eda808ddaf19d54e614711548f.jpg', 1551926462),
(22, '范彦伟', '副主任医师', 9, '\r\n长期在放射科一线工作。积累了丰富的X线技术学、诊断学、CT技术学、诊断学和MRI技术学、诊断学经验。尤其擅长儿童中枢神经系统，儿童骨关节系统，儿童呼吸系统的MRI及CT检查和诊断在国内著名刊...', '儿童中枢神经系统，儿童骨关节系统，儿童呼吸系统的MRI及CT检查和诊断', '02b', '20190307/4f3aa0e01350cada9353f7a943b86ced.jpg', 1551926508),
(23, '程元平', '主任医师', 9, '超声诊断主任医师，超声科主任，大学本科，学士学位。 从事超声诊断工作十余年,曾先后到北京市儿童医院、天津市肿瘤医院及河南省妇幼保健院超声科进修学习。具有扎实的理论基础和丰富的临床经验，率先在...', '胎儿四维成像、胎儿先天畸形筛查、乳腺肿瘤、妇产科肿瘤及小儿急腹症的超声诊断', '01a,01b,02a,02b,03a,03b,04a,04b,05a,05b,06a', '20190307/8e5e60fc39335a28f3a4385280eaedf4.png', 1551926795);

